import React, { useState, useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom'; // For navigation
import { ThemeContext } from '../ThemeContext'; // Import Theme Context
import { motion } from 'framer-motion'; // For animations
import './component-styles/MenuBar.css'; // External CSS for styling

const MenuBar = () => {
  const { theme } = useContext(ThemeContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State for menu visibility
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State for login status

  // Check localStorage for login state when MenuBar mounts
  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setIsLoggedIn(true);
    }

    // Retrieve menu state from localStorage
    const storedMenuState = localStorage.getItem('isMenuOpen');
    if (storedMenuState === 'true') {
      setIsMenuOpen(true);
    }
  }, []);

  // Toggle menu visibility and store state
  const toggleMenu = () => {
    const newMenuState = !isMenuOpen;
    setIsMenuOpen(newMenuState);
    localStorage.setItem('isMenuOpen', newMenuState); // Save the menu state
  };

  const menuVariants = {
    hidden: { opacity: 0, x: -100 },
    visible: { opacity: 1, x: 0 },
  };

  return (
    <div className={`menu-bar-container ${theme}`}>
      <div className="menu-icon" onClick={toggleMenu}>
        <span className="material-icons">menu</span>
      </div>

      {isMenuOpen && (
        <motion.nav
          className="menu-options"
          initial="hidden"
          animate="visible"
          variants={menuVariants}
          transition={{ type: 'tween', stiffness: 300 }}
        >
           <ul>
          <li><NavLink to="/home/query" activeClassName="active">AI Lawyer</NavLink></li>
          <li><NavLink to="/bareacts" activeClassName="active">Bare Acts</NavLink></li>
          <li><NavLink to="/home/database" activeClassName="active">Database</NavLink></li>
          <li>
            {isLoggedIn ? (
             <NavLink to="/home/login" activeClassName="active">Logged In</NavLink>
            ) : (
              <NavLink to="/home/login" activeClassName="active">Login</NavLink>
            )}
          </li>
          <li><NavLink to="/home/settings" activeClassName="active">Settings</NavLink></li>
        </ul>
        </motion.nav>
      )}
    </div>
  );
};

export default MenuBar;
