import React, { useContext, useEffect, useState } from 'react'; 
import { ThemeContext } from '../ThemeContext'; 
import Sidebar from '../components/Sidebar'; 
import MenuBar from '../components/MenuBar'; // Import MenuBar component for mobile

const Database = () => {
  const { theme } = useContext(ThemeContext);

  // State to store cases
  const [cases, setCases] = useState([]);

  // State to track screen width for responsiveness
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  // Effect to fetch data
  useEffect(() => {
    fetch('https://chatbot-for-fir-backend.vercel.app/api/server/cases')
      .then((response) => response.json())
      .then((data) => setCases(data))
      .catch((error) => console.error('Error fetching cases:', error));
  }, []);

  // Effect to update mobile state on window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);
    
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className={`database-container ${theme} min-h-screen flex flex-col`}>
      {/* Render Sidebar or MenuBar based on screen size */}
      {isMobile ? <MenuBar /> : <Sidebar />}
      
      <main className="main-content flex-grow p-8">
        <h2 className="database-title text-center text-3xl font-bold text-gray-800 mb-8">Case Database</h2>
        
        <div className="case-cards grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {cases.map((caseItem) => (
            <div key={caseItem.srNo} className="case-card p-6 border rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300 bg-white">
              <h3 className="text-xl font-semibold text-gray-700">Sr No: {caseItem.srNo}</h3>
              <p className="mt-2 text-gray-600">{caseItem.description}</p>
              
              <div className="status mt-4">
                <span className={`status-badge inline-block py-1 px-4 rounded-full text-white text-sm ${
                  caseItem.status === 'Open' ? 'bg-green-500' : 
                  caseItem.status === 'Closed' ? 'bg-red-500' : 
                  'bg-yellow-500'}`}
                >
                  {caseItem.status}
                </span>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Database;
