import React, { useState, useEffect, useMemo } from 'react';
import Sidebar from '../components/Sidebar';
import '../styles/Query.css';
import { FaMicrophone } from 'react-icons/fa';
import Footer from '../components/Footer';

const Query = () => {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('Response will appear here...');
  const [isListening, setIsListening] = useState(false);
  const [queryHistory, setQueryHistory] = useState([]);

  // Web Speech API initialization
  const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

  const recognition = useMemo(() => {
    const recog = new SpeechRecognition();
    recog.continuous = false;
    recog.interimResults = false;
    recog.lang = 'en-US';
    return recog;
  }, [SpeechRecognition]);

  useEffect(() => {
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuery(transcript);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
      setIsListening(false);
    };
  }, [recognition]);

  const handleMicClick = () => {
    if (isListening) {
      recognition.stop();
    } else {
      recognition.start();
    }
    setIsListening(!isListening);
  };

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleQuerySubmit = async (e) => {
    e.preventDefault();

    const fullQuery = `${query}. You are an AI assistant with comprehensive knowledge of the Indian Constitution, including all applicable acts and sections. Your task is to assist law enforcement officers in filing an FIR by identifying and listing all relevant acts and sections that apply to the case presented. For each act or section, provide a brief explanation of its significance and how it applies to the case. Ensure that all applicable legal provisions are mentioned to help the officer file a thorough and accurate FIR.`;

    try {
      const response = await fetch('https://chatbot-for-fir-backend.vercel.app/api/gemini/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: fullQuery }),
      });

      const data = await response.json();
      setResponse(parseMarkdownToHTML(data.response || 'No response received'));

      // Store query history
      setQueryHistory((prevHistory) => [
        { query: query, response: data.response },
        ...prevHistory,
      ]);
    } catch (error) {
      console.error('Error fetching the response:', error);
      setResponse('Error occurred while fetching the response');
    }

    setQuery(''); // Clear the input field after submission
  };

  const parseMarkdownToHTML = (text) => {
    return text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
  };

  return (
    <div className="query-page-container">
      {/* Sidebar */}
      <Sidebar />

      <main className="query-page-main-content">
        <h2 className="query-page-title">Ask a Query</h2>

        {/* Introduction Section */}
        <section className="query-introduction">
          <p>
            Welcome to the LawAI Query Page. Ask any legal question related to FIR filing, and our AI-powered assistant will provide you with the relevant sections and acts from the Indian Constitution and related laws. This tool is specifically designed to assist law enforcement officers.
          </p>
        </section>

        {/* Query Input Section */}
        <div className="query-input-container">
          <div className="query-input-box">
            <input
              type="text"
              placeholder="Ask your query..."
              value={query}
              onChange={handleInputChange}
              className="query-text-input"
            />
            <FaMicrophone
              className={`query-mic-icon ${isListening ? 'listening' : ''}`}
              onClick={handleMicClick}
            />
            <button onClick={handleQuerySubmit} className="query-submit-btn">
              Enter
            </button>
          </div>
        </div>

        {/* Response Box */}
        <div className="query-response-box">
          <pre dangerouslySetInnerHTML={{ __html: response }} />
        </div>

        {/* Popular Queries Section */}
        <section className="popular-queries py-12">
          <h3 className="text-2xl font-semibold text-blue-800">Popular Queries</h3>
          <ul className="text-gray-600 mt-4">
            <li>How do I file an FIR for theft?</li>
            <li>What are the legal provisions for domestic violence cases?</li>
            <li>What sections apply to a cybercrime complaint?</li>
          </ul>
        </section>

        {/* Query History Section */}
        <section className="query-history py-12">
          <h3 className="text-2xl font-semibold text-blue-800">Query History</h3>
          {queryHistory.length === 0 ? (
            <p>No previous queries.</p>
          ) : (
            <ul className="text-gray-600 mt-4">
              {queryHistory.map((historyItem, index) => (
                <li key={index} className="mb-4">
                  <strong>Query:</strong> {historyItem.query} <br />
                  <strong>Response:</strong> <span dangerouslySetInnerHTML={{ __html: historyItem.response }} />
                </li>
              ))}
            </ul>
          )}
        </section>

        {/* Helpful Resources Section */}
        <section className="helpful-resources py-12">
          <h3 className="text-2xl font-semibold text-blue-800">Helpful Resources</h3>
          <ul className="text-gray-600 mt-4">
            <li><a href="/legal-guidelines" className="hover:underline">Legal Guidelines for FIR Filing</a></li>
            <li><a href="/indian-constitution" className="hover:underline">Indian Constitution Overview</a></li>
            <li><a href="/faq" className="hover:underline">Frequently Asked Questions</a></li>
          </ul>
        </section>

      </main>

      <Footer />
    </div>
  );
};

export default Query;
