import React from 'react';
import { useNavigate } from 'react-router-dom';
import KeyFeatures from '../components/KeyFeatures';
import '../styles/Landing.css'; // External CSS for styling

const Landing = () => {
  const navigate = useNavigate();

  const handleDownload = () => {
    navigate('/download');
  };

  return (
    <div className="landing bg-gray-100 text-gray-900">
      <header className="header bg-blue-900 text-white py-4">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <div className="text-lg font-semibold">LawAI - Government of India</div>
          <button
            className="material-icons download-icon text-lg"
            onClick={handleDownload}
            style={{ cursor: 'pointer' }}
          >
            file_download
          </button>
        </div>
      </header>

      <section className="hero bg-white py-10">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-3xl font-bold text-blue-900 mb-4">
            Empowering Law Enforcement with AI
          </h1>
          <p className="text-gray-700 mb-6">
            Instant access to accurate legal information for writing flawless FIRs.
          </p>
          <a href="/home/query" className="btn primary-btn text-white bg-green-600 px-4 py-2 rounded hover:bg-green-700 transition">
            Get Started
          </a>
        </div>
      </section>

      <section id="features" className="features bg-gray-100 py-10">
        <div className="container mx-auto px-4">
          <KeyFeatures />
        </div>
      </section>

      <footer className="footer bg-blue-900 text-white py-4">
        <div className="container mx-auto px-4 text-center">
          <p>Â© 2024 Government of India. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Landing;
