import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ThemeContext } from '../ThemeContext'; // Import the ThemeContext
import './component-styles/MenuBar.css';

  // Check localStorage for login state when MenuBar mounts
  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setIsLoggedIn(true); // User is logged in
    }
  }, []);

  // Check localStorage for theme and menu state
  useEffect(() => {
    const storedTheme = localStorage.getItem('theme');
    if (storedTheme) {
      setTheme(storedTheme);
    }
    const storedMenuState = localStorage.getItem('isMenuOpen');
    if (storedMenuState === 'true') {
      setIsMenuOpen(true);
    }
  }, []);
const MenuBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme } = useContext(ThemeContext); // Use the theme from the context

  useEffect(() => {
    const storedMenuState = localStorage.getItem('isMenuOpen');
    if (storedMenuState === 'true') {
      setIsMenuOpen(true);
    }
  }, []);

  const toggleMenu = () => {
    const newMenuState = !isMenuOpen;
    setIsMenuOpen(newMenuState);
    localStorage.setItem('isMenuOpen', newMenuState);
  };

  const menuVariants = {
    hidden: { x: -250 },
    visible: { x: 0 },
  };

  // Set the background color for the menu based on the theme
  const menuStyle = {
    backgroundColor: theme === 'light' ? '#ffffff' : '#1e1e1e', // White in light mode, dark in dark mode
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
  };

  return (
    <div className={`menu-bar-container ${theme}`}>
      <div className="menu-icon" onClick={toggleMenu}>
        <span className="material-icons">menu</span>
      </div>

      {isMenuOpen && (
        <motion.nav
          className="menu-options"
          initial="hidden"
          animate="visible"
          variants={menuVariants}
          transition={{ type: 'tween', stiffness: 300 }}
          style={menuStyle}
        >
          <div className="menu-header">
            <h1>LawAI</h1>
            <span className="material-icons">gavel</span>
          </div>
          <ul>
            <li><NavLink to="/home/query" activeClassName="active">AI Lawyer</NavLink></li>
            <li><NavLink to="/bareacts" activeClassName="active">Bare Acts</NavLink></li>
            <li><NavLink to="/home/database" activeClassName="active">Database</NavLink></li>
            <li><NavLink to="/home/login" activeClassName="active">Login</NavLink></li>
            <li><NavLink to="/home/settings" activeClassName="active">Settings</NavLink></li>
          </ul>
        </motion.nav>
      )}
    </div>
  );
};

export default MenuBar;
