import React, { useContext, useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { ThemeContext } from '../ThemeContext';
import { motion } from 'framer-motion';
import './component-styles/Sidebar.css'; // Updated CSS for the menubar styling

const Sidebar = () => {
  const { theme } = useContext(ThemeContext);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [name, setName] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');

  useEffect(() => {
    const storedName = localStorage.getItem('name');
    const storedBadgeNumber = localStorage.getItem('badgeNumber');
    if (storedName && storedBadgeNumber) {
      setName(storedName);
      setBadgeNumber(storedBadgeNumber);
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <motion.nav
      className={`sidebar ${theme} bg-gradient-to-r from-blue-600 to-blue-800 text-white min-h-screen p-6 flex flex-col shadow-lg`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: 'tween', stiffness: 300 }}
    >
      {/* Sidebar Header */}
      <div className="flex flex-col items-center mb-8">
        {/* Logo */}
        <div className="sidebar-logo mb-6 text-center">
          <h1 className="text-3xl font-bold text-white">LawAI</h1>
          <span className="material-icons text-4xl text-white">gavel</span>
        </div>

        {/* Navigation Links */}
        <div className="sidebar-links flex flex-col w-full space-y-4">
          <NavLink
            to="/home/query"
            activeClassName="active"
            className="sidebar-link py-2 px-6 rounded-lg hover:bg-blue-500 hover:scale-105 transition-all"
          >
            AI Lawyer
          </NavLink>
          <NavLink
            to="/bareacts"
            activeClassName="active"
            className="sidebar-link py-2 px-6 rounded-lg hover:bg-blue-500 hover:scale-105 transition-all"
          >
            Bare Acts
          </NavLink>
          <NavLink
            to="/home/database"
            activeClassName="active"
            className="sidebar-link py-2 px-6 rounded-lg hover:bg-blue-500 hover:scale-105 transition-all"
          >
            Database
          </NavLink>
          <NavLink
            to="/home/settings"
            activeClassName="active"
            className="sidebar-link py-2 px-6 rounded-lg hover:bg-blue-500 hover:scale-105 transition-all"
          >
            Settings
          </NavLink>
          <NavLink
            to="/home/login"
            activeClassName="active"
            className="sidebar-link py-2 px-6 rounded-lg hover:bg-blue-500 hover:scale-105 transition-all"
          >
            {isLoggedIn ? 'Logged In' : 'Login'}
          </NavLink>
        </div>
      </div>

      {/* User Info (if logged in) */}
      {isLoggedIn && (
        <div className="mt-auto bg-white text-gray-900 rounded-xl p-6 shadow-lg hover:shadow-2xl transition-all">
          <div className="flex items-center space-x-4">
            <img
              src="https://www.pngall.com/wp-content/uploads/12/Avatar-PNG-Images.png"
              alt="User Profile"
              className="user-avatar rounded-full w-16 h-16 object-cover"
            />
            <div className="user-details text-left">
              <p className="user-name text-xl font-semibold">{name}</p>
              <p className="user-badge text-sm text-gray-500">Badge No: {badgeNumber}</p>
            </div>
          </div>
        </div>
      )}
    </motion.nav>
  );
};

export default Sidebar;
