import React, { useState, useContext } from 'react';
import { ThemeContext } from '../ThemeContext'; // Import Theme Context
import Sidebar from '../components/Sidebar'; // Import Sidebar component
import '../styles/Query.css'; // External CSS for styling

const Query = () => {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const [userInput, setUserInput] = useState('');
  const [response, setResponse] = useState('Awaiting your query...');

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Placeholder logic for response (use constants for now)
    setResponse(`Response from database: ${userInput}`);
  };

  const handleVoiceInput = () => {
    // Placeholder function for voice input
    console.log('Voice input activated');
  };

  return (
    <div className={`query-container-${theme}`}>
      <Sidebar />
      <main className={`query-main-content-${theme}`}>
        <div className={`query-theme-toggle-${theme}`} onClick={toggleTheme}>
          <span className="material-icons">
            {theme === 'light' ? 'dark_mode' : 'light_mode'}
          </span>
        </div>
        <div className={`query-response-box-${theme}`}>
          <p className={`query-response-text-${theme}`}>{response}</p>
        </div>
        <form onSubmit={handleSubmit} className={`query-form-${theme}`}>
          <div className={`query-input-box-${theme}`}>
            <input
              type="text"
              value={userInput}
              onChange={handleInputChange}
              className={`query-input-${theme}`}
              placeholder="Type your query..."
              required
            />
            <button
              type="button"
              onClick={handleVoiceInput}
              className={`query-voice-btn-${theme}`}
            >
              <span className="material-icons">mic</span>
            </button>
          </div>
          <button type="submit" className={`query-submit-btn-${theme}`}>
            Enter
          </button>
        </form>
      </main>
    </div>
  );
};

export default Query;
