import React, { useState, useEffect, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { motion } from 'framer-motion';
import { ThemeContext } from '../ThemeContext';
import './component-styles/MenuBar.css';

const MenuBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState('');
  const [name, setName] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    const storedMenuState = localStorage.getItem('isMenuOpen');
    if (storedMenuState === 'true') {
      setIsMenuOpen(true);
    }

    // Retrieve user login data from localStorage
    const storedUserId = localStorage.getItem('userId');
    const storedName = localStorage.getItem('name');
    const storedBadgeNumber = localStorage.getItem('badgeNumber');
    const loggedInStatus = localStorage.getItem('isLoggedIn') === 'true';

    if (loggedInStatus && storedUserId && storedName && storedBadgeNumber) {
      setIsLoggedIn(true);
      setUserId(storedUserId);
      setName(storedName);
      setBadgeNumber(storedBadgeNumber);
    }
  }, []);

  const toggleMenu = () => {
    const newMenuState = !isMenuOpen;
    setIsMenuOpen(newMenuState);
    localStorage.setItem('isMenuOpen', newMenuState);
  };

  const menuVariants = {
    hidden: { x: -250 },
    visible: { x: 0 },
  };

  const menuStyle = {
    backgroundColor: theme === 'light' ? '#ffffff' : '#1e1e1e',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
  };

  return (
    <div className={`menu-bar-container ${theme}`}>
      <div className="menu-icon" onClick={toggleMenu}>
        <span className="material-icons">menu</span>
      </div>

      {isMenuOpen && (
        <motion.nav
          className="menu-options"
          initial="hidden"
          animate="visible"
          variants={menuVariants}
          transition={{ type: 'tween', stiffness: 300 }}
          style={menuStyle}
        >
          <div className="menu-header">
            <h1>LawAI</h1>
            <span className="material-icons">gavel</span>
          </div>

          {isLoggedIn && (
            <div className="user-details">
              <p>Name: {name}</p>
              <p>ID: {userId}</p>
              <p>Badge No: {badgeNumber}</p>
            </div>
          )}

          <ul>
            <li><NavLink to="/home/query" activeClassName="active">AI Lawyer</NavLink></li>
            <li><NavLink to="/bareacts" activeClassName="active">Bare Acts</NavLink></li>
            <li><NavLink to="/home/database" activeClassName="active">Database</NavLink></li>
            <li>
              <NavLink to="/home/login" activeClassName="active">
                {isLoggedIn ? 'Logged In' : 'Login'}
              </NavLink>
            </li>
            <li><NavLink to="/home/settings" activeClassName="active">Settings</NavLink></li>
          </ul>
        </motion.nav>
      )}
    </div>
  );
};

export default MenuBar;
