// Query.js
import React, { useContext, useState } from 'react';
import { ThemeContext } from '../ThemeContext'; // Import Theme Context
import Sidebar from '../components/Sidebar'; // Import Sidebar component
import '../styles/Query.css'; // External CSS for styling
import { FaMicrophone } from 'react-icons/fa'; // Importing a microphone icon

const Query = () => {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('Response will appear here...');

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleQuerySubmit = () => {
    // Simulated response from the database for now
    setResponse(`You asked: "${query}", here is a simulated response.`);
    setQuery('');
  };

  return (
    <div className={`query-container ${theme}`}>
      <Sidebar />
      <main className="main-content">
        <div className="theme-toggle" onClick={toggleTheme}>
          <span className="material-icons">
            {theme === 'light' ? 'dark_mode' : 'light_mode'}
          </span>
        </div>

        <h2 className="query-title">Ask a Query</h2>

        {/* Response Field */}
        <div className={`response-box ${theme}`}>
          {response}
        </div>

        {/* Input Section */}
        <div className="input-container">
          <div className={`input-box ${theme}`}>
            <input
              type="text"
              placeholder="Ask your query..."
              value={query}
              onChange={handleInputChange}
              className={`text-input ${theme}`}
            />
            <FaMicrophone className={`mic-icon ${theme}`} />
          </div>
          <button onClick={handleQuerySubmit} className={`btn submit-btn ${theme}`}>Enter</button>
        </div>
      </main>
    </div>
  );
};

export default Query;
