import React from 'react';
import '../styles/Download.css'; // Importing the CSS for styling

const Download = () => {
  const handleDownload = () => {
    // Here you can replace the URL with the actual path of your APK file
    window.location.href = 'path/to/your/application.apk';
  };

  return (
    <div className="download-page">
      <div className="container">
        <h1 className="fade-in">Download LawAI App</h1>
        <p className="fade-in">Get instant access to legal information and guidance at your fingertips.</p>
        
        <div className="download-button-container fade-in">
          <button className="btn download-btn" onClick={handleDownload}>
            Download APK
          </button>
        </div>
        
        <div className="app-preview fade-in">
          <img src="../images/app-preview.png" alt="LawAI App Preview" />
        </div>
      </div>
    </div>
  );
};

export default Download;
