import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider } from './ThemeContext';
import Landing from './pages/Landing';
import Home from './Home';
import Settings from './pages/Settings';
import Login from './pages/Login';
import Database from './pages/Database';
import Query from './pages/Query';
import BareActs from './pages/BareActs';
import MenuBar from './components/MenuBar'; // Include MenuBar
import Sidebar from './components/Sidebar'; // Include Sidebar (if needed)

function App() {
  return (
    <ThemeProvider>
      <Router>
        <div className="App">
          {/* If you want the MenuBar and Sidebar to be on every page */}
          <MenuBar />
          <Sidebar />
          
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/home" element={<Home />} />
            <Route path="/home/settings" element={<Settings />} />
            <Route path="/home/login" element={<Login />} />
            <Route path="/home/database" element={<Database />} />
            <Route path="/home/query" element={<Query />} />
            <Route path="/bareacts" element={<BareActs />} />
            {/* Add a catch-all route for 404 handling */}
            <Route path="*" element={<div>404 Not Found</div>} />
          </Routes>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
