import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { motion } from 'framer-motion';
import './component-styles/MenuBar.css'; // Updated CSS for the menubar styling

const MenuBar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [name, setName] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');

  useEffect(() => {
    const storedName = localStorage.getItem('name');
    const storedBadgeNumber = localStorage.getItem('badgeNumber');
    if (storedName && storedBadgeNumber) {
      setName(storedName);
      setBadgeNumber(storedBadgeNumber);
      setIsLoggedIn(true);
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const menuVariants = {
    hidden: { x: -250 },
    visible: { x: 0 },
  };

  return (
    <div className="menu-bar-container light">
      <div className="menu-icon" onClick={toggleMenu}>
        <span className="material-icons text-white">menu</span>
      </div>

      {isMenuOpen && (
        <motion.nav
          className="menu-options"
          initial="hidden"
          animate="visible"
          variants={menuVariants}
          transition={{ type: 'tween', stiffness: 300 }}
          style={{
            backgroundColor: '#ffffff', // Fixed light mode background
            borderRadius: '8px',
            padding: '20px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
          }}
        >
          <div className="sidebar-content bg-gradient-to-r from-blue-900 to-blue-600 text-white py-8">
            {/* Logo and Title */}
            <div className="sidebar-logo flex items-center justify-between px-6 mb-8">
              <h1 className="text-5xl font-bold text-white">LawAI</h1>
              <span className="material-icons text-white">gavel</span>
            </div>

            {/* User Info and Navigation Links */}
            <div className="sidebar-links flex flex-col space-y-4">
              <NavLink to="/home/query" activeClassName="active" className="sidebar-link">AI Lawyer</NavLink>
              <NavLink to="/bareacts" activeClassName="active" className="sidebar-link">Bare Acts</NavLink>
              <NavLink to="/home/database" activeClassName="active" className="sidebar-link">Database</NavLink>
              <NavLink to="/home/settings" activeClassName="active" className="sidebar-link">Settings</NavLink>

              {/* Conditional Rendering of Avatar or Text */}
              <NavLink to="/home/login" activeClassName="active" className="sidebar-link flex items-center space-x-2">
                {isLoggedIn ? (
                  <>
                    <img
                      src="https://www.pngall.com/wp-content/uploads/12/Avatar-PNG-Images.png"
                      alt="User Profile"
                      className="user-avatar rounded-full w-10 h-10 object-cover"
                    />
                    <span>{name}</span>
                  </>
                ) : (
                  'Login'
                )}
              </NavLink>
            </div>
          </div>
        </motion.nav>
      )}
    </div>
  );
};

export default MenuBar;
