import React, { useState, useEffect, useMemo } from 'react';
import Sidebar from '../components/Sidebar';
import '../styles/Query.css';
import { FaMicrophone } from 'react-icons/fa';
import Footer from '../components/Footer';

const Query = () => {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('Response will appear here...');
  const [isListening, setIsListening] = useState(false);

  // Web Speech API initialization
  const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

  // Wrap the initialization in useMemo to prevent re-creation on every render
  const recognition = useMemo(() => {
    const recog = new SpeechRecognition();
    recog.continuous = false;
    recog.interimResults = false;
    recog.lang = 'en-US';
    return recog;
  }, [SpeechRecognition]);

  useEffect(() => {
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuery(transcript);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
      setIsListening(false);
    };
  }, [recognition]);

  const handleMicClick = () => {
    if (isListening) {
      recognition.stop();
    } else {
      recognition.start();
    }
    setIsListening(!isListening);
  };

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleQuerySubmit = async (e) => {
    e.preventDefault();

    const fullQuery = `${query}. You are an AI assistant with comprehensive knowledge of the Indian Constitution, including all applicable acts and sections. Your task is to assist law enforcement officers in filing an FIR by identifying and listing all relevant acts and sections that apply to the case presented. For each act or section, provide a brief explanation of its significance and how it applies to the case. Ensure that all applicable legal provisions are mentioned to help the officer file a thorough and accurate FIR.`;

    try {
      const response = await fetch('https://chatbot-for-fir-backend.vercel.app/api/gemini/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: fullQuery }),
      });

      const data = await response.json();
      setResponse(parseMarkdownToHTML(data.response || 'No response received'));
    } catch (error) {
      console.error('Error fetching the response:', error);
      setResponse('Error occurred while fetching the response');
    }

    setQuery(''); // Clear the input field after submission
  };

  const parseMarkdownToHTML = (text) => {
    return text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');
  };

  return (
    <div className="query-page-container">
      {/* Sidebar */}
      <Sidebar />

      <main className="query-page-main-content bg-white py-16 px-8">
  <h2 className="query-page-title text-3xl font-semibold text-blue-900 text-center mb-8">
    Ask a Query
  </h2>

  {/* Query Response Box */}
  <div className="query-response-box bg-light-gray p-8 rounded-lg shadow-md mb-10 max-w-3xl mx-auto border border-gray-300">
    <pre dangerouslySetInnerHTML={{ __html: response }} className="text-gray-800 font-medium leading-relaxed"></pre>
  </div>

  {/* Query Input Section */}
  <div className="query-input-container max-w-3xl mx-auto bg-white p-8 rounded-lg shadow-md border border-gray-300">
    <div className="flex flex-col md:flex-row items-center space-y-6 md:space-y-0">
      {/* Input Box */}
      <div className="query-input-box flex-grow mr-4">
        <input
          type="text"
          placeholder="Ask your query..."
          value={query}
          onChange={handleInputChange}
          className="query-text-input w-full p-4 border border-gray-400 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-600"
        />
      </div>

      {/* Microphone Icon */}
      <div className="query-mic-icon-container flex items-center justify-center">
        <FaMicrophone
          className={`query-mic-icon text-3xl cursor-pointer transition-all duration-200 transform ${isListening ? 'text-blue-600 scale-110' : 'text-gray-600'}`}
          onClick={handleMicClick}
        />
      </div>

      {/* Submit Button */}
      <div className="query-submit-btn-container mt-4 md:mt-0">
        <button
          onClick={handleQuerySubmit}
          className="query-submit-btn bg-orange-600 text-white py-3 px-6 rounded-md shadow-md hover:bg-orange-700 transition duration-200"
        >
          Submit
        </button>
      </div>
    </div>
  </div>
</main>



      <Footer />
    </div>
  );
};

export default Query;
