import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar'; // Import Sidebar component
import '../styles/Login.css'; // External CSS for styling

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // State for name input
  const [badgeNumber, setBadgeNumber] = useState(''); // State for badge number input
  const [isSignup, setIsSignup] = useState(false); // State for toggling between login and signup
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State for login status
  const [userId, setUserId] = useState(''); // State for storing user ID

  // Check localStorage for login state when component mounts
  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    const storedName = localStorage.getItem('name');
    const storedBadgeNumber = localStorage.getItem('badgeNumber');
    const storedEmail = localStorage.getItem('email');
    if (storedUserId && storedName && storedBadgeNumber && storedEmail) {
      setIsLoggedIn(true);
      setUserId(storedUserId);
      setName(storedName);
      setBadgeNumber(storedBadgeNumber);
      setEmail(storedEmail);
    }
  }, []);

  const handleLogin = (e) => {
    e.preventDefault();
    setUserId(email);
    setIsLoggedIn(true);
    // Store user data in localStorage
    localStorage.setItem('userId', email); // Store userId in localStorage
    localStorage.setItem('name', name); // Store name in localStorage
    localStorage.setItem('badgeNumber', badgeNumber); // Store badge number in localStorage
    localStorage.setItem('email', email); // Store email in localStorage
    console.log('Logging in with', { email, password, name, badgeNumber });
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserId('');
    setName('');
    setBadgeNumber('');
    setEmail('');
    // Clear user data from localStorage
    localStorage.removeItem('userId');
    localStorage.removeItem('name');
    localStorage.removeItem('badgeNumber');
    localStorage.removeItem('email');
    console.log('Logged out successfully');
  };

  const toggleForm = () => {
    setIsSignup(!isSignup);
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar isLoggedIn={isLoggedIn} userId={userId} className="w-64 bg-blue-900 text-white" />
      
      {/* Main Content */}
      <main className="flex-1 p-8 mt-16 ml-64">
        {isLoggedIn ? (
          <div className="flex flex-col items-center justify-center space-y-8">
            <h2 className="text-4xl font-semibold text-blue-800">You are logged in</h2>

            {/* Display user details in a visually appealing format */}
            <div className="space-y-4 text-lg">
              <p><strong>Name:</strong> {name}</p>
              <p><strong>Email:</strong> {email}</p>
              <p><strong>Badge Number:</strong> {badgeNumber}</p>
            </div>

            {/* Logout Button */}
            <button 
              className="bg-red-600 text-white py-2 px-4 rounded-full hover:bg-red-700 transition"
              onClick={handleLogout}
            >
              Log Out
            </button>
          </div>
        ) : (
          <div className="flex flex-col items-center justify-center space-y-8">
            <h2 className="text-4xl font-semibold text-blue-900">{isSignup ? 'Sign Up' : 'Login'}</h2>
            <form onSubmit={handleLogin} className="w-full max-w-md bg-white p-8 rounded-lg shadow-lg space-y-6">
              {isSignup && (
                <>
                  <div className="flex flex-col">
                    <label htmlFor="name" className="text-sm font-semibold text-gray-700">Name</label>
                    <input
                      type="text"
                      id="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      className="mt-2 p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    />
                  </div>
                  <div className="flex flex-col">
                    <label htmlFor="badgeNumber" className="text-sm font-semibold text-gray-700">Badge Number</label>
                    <input
                      type="text"
                      id="badgeNumber"
                      value={badgeNumber}
                      onChange={(e) => setBadgeNumber(e.target.value)}
                      className="mt-2 p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    />
                  </div>
                </>
              )}
              <div className="flex flex-col">
                <label htmlFor="email" className="text-sm font-semibold text-gray-700">Email</label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="mt-2 p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <div className="flex flex-col">
                <label htmlFor="password" className="text-sm font-semibold text-gray-700">Password</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="mt-2 p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              <button type="submit" className="w-full bg-blue-600 text-white py-3 rounded-full hover:bg-blue-700 transition">
                {isSignup ? 'Sign Up' : 'Login'}
              </button>
            </form>
            <p className="text-center text-sm text-gray-600">
              {isSignup ? 'Already a user? ' : 'Not a user? '}
              <span 
                className="text-blue-600 cursor-pointer hover:underline" 
                onClick={toggleForm}
              >
                {isSignup ? 'Login' : 'Sign Up'}
              </span>
            </p>
          </div>
        )}
      </main>
    </div>
  );
};

export default Login;
