// Query.js
import React, { useContext, useState, useEffect } from 'react';
import { ThemeContext } from '../ThemeContext';
import Sidebar from '../components/Sidebar';
import '../styles/Query.css';
import { FaMicrophone } from 'react-icons/fa';

const Query = () => {
  const { theme, toggleTheme } = useContext(ThemeContext);
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('Response will appear here...');
  const [isListening, setIsListening] = useState(false);

  // Web Speech API initialization
  const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
  const recognition = new SpeechRecognition();

  recognition.continuous = false;
  recognition.interimResults = false;
  recognition.lang = 'en-US';

  useEffect(() => {
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuery(transcript);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
      setIsListening(false);
    };
  }, [recognition]);

  const handleMicClick = () => {
    if (isListening) {
      recognition.stop();
    } else {
      recognition.start();
    }
    setIsListening(!isListening);
  };

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleQuerySubmit = () => {
    setResponse(`You asked: "${query}", here is a simulated response.`);
    setQuery('');
  };

  return (
    <div className={`query-container ${theme}`}>
      <Sidebar />
      <main className="main-content">
        <div className="theme-toggle" onClick={toggleTheme}>
          <span className="material-icons">
            {theme === 'light' ? 'dark_mode' : 'light_mode'}
          </span>
        </div>

        <h2 className="query-title">Ask a Query</h2>

        {/* Response Field */}
        <div className={`response-box ${theme}`}>
          {response}
        </div>

        {/* Input Section */}
        <div className="input-container">
          <div className={`input-box ${theme}`}>
            <input
              type="text"
              placeholder="Ask your query..."
              value={query}
              onChange={handleInputChange}
              className={`text-input ${theme}`}
            />
            <FaMicrophone
              className={`mic-icon ${isListening ? 'listening' : ''} ${theme}`}
              onClick={handleMicClick}
            />
          </div>
          <button onClick={handleQuerySubmit} className={`btn submit-btn ${theme}`}>
            Enter
          </button>
        </div>
      </main>
    </div>
  );
};

export default Query;
