import React from 'react';
import { Link } from 'react-router-dom';

const Footer = () => {
  return (
    <footer className="footer bg-blue-900 text-white py-12 mt-12">
      <div className="container mx-auto px-6">
        <div className="flex flex-col md:flex-row justify-between items-center text-center md:text-left">
          {/* Copyright and Team Link */}
          <p className="text-lg md:text-xl font-medium">
            &copy; 2024 <Link to="/team" className="text-yellow-400 hover:text-yellow-500 transition-colors">CODE-A-COLA</Link>. All rights reserved.
          </p>

          {/* Footer Navigation Links */}
          <nav className="flex flex-col md:flex-row mt-6 md:mt-0 text-sm space-y-2 md:space-x-6 md:space-y-0">
            <Link to="/privacypolicy" className="text-white hover:text-yellow-400 transition-colors">Privacy Policy</Link>
            <Link to="/terms" className="text-white hover:text-yellow-400 transition-colors">Terms & Conditions</Link>
            <Link to="/accessibility" className="text-white hover:text-yellow-400 transition-colors">Accessibility</Link>
          </nav>
        </div>
        <div className="mt-6 text-center text-xs text-gray-400">
          <p>Made with ❤️ by CODE-A-COLA for a smarter tomorrow.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
