import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { motion } from 'framer-motion';
import './component-styles/Sidebar.css'; // Updated CSS for the menubar styling

const Sidebar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [name, setName] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // State for mobile sidebar toggle

  useEffect(() => {
    const storedName = localStorage.getItem('name');
    const storedBadgeNumber = localStorage.getItem('badgeNumber');
    if (storedName && storedBadgeNumber) {
      setName(storedName);
      setBadgeNumber(storedBadgeNumber);
      setIsLoggedIn(true);
    }
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <motion.nav>
      <div
        className={`sidebar-content bg-gradient-to-r from-blue-900 to-blue-600 text-white py-8 ${isSidebarOpen ? 'block' : 'hidden'} md:block`} // Show sidebar on large screens
      >
        {/* Logo and Title */}
        <div className="sidebar-logo flex items-center space-x-4">
          <h1 className="text-3xl font-bold text-white ml-6">LawAI</h1>
          <span className="material-icons text-white">gavel</span>
        </div>

        {/* Navigation Links */}
        <div className="sidebar-links flex flex-col space-y-4 mt-6">
          <NavLink to="/home/query" activeClassName="active" className="sidebar-link">
            AI Lawyer
          </NavLink>
          <NavLink to="/bareacts" activeClassName="active" className="sidebar-link">
            Bare Acts
          </NavLink>
          <NavLink to="/home/database" activeClassName="active" className="sidebar-link">
            Database
          </NavLink>
          <NavLink to="/home/settings" activeClassName="active" className="sidebar-link">
            Settings
          </NavLink>

          {/* Conditional Rendering of Avatar or Text */}
          <NavLink to="/home/login" activeClassName="active" className="sidebar-link">
            {isLoggedIn ? (
              <img
                src="https://www.pngall.com/wp-content/uploads/12/Avatar-PNG-Images.png"
                alt="User Profile"
                className="user-avatar rounded-full w-8 h-8 object-cover"
              />
            ) : (
              'Login'
            )}
          </NavLink>
        </div>
      </div>

      {/* Mobile Hamburger Menu */}
      <div className="md:hidden flex items-center justify-between px-4 py-2">
        <button onClick={toggleSidebar} className="text-white">
          <span className="material-icons">menu</span>
        </button>
      </div>
    </motion.nav>
  );
};

export default Sidebar;
